package quiz.app;import javax.swing.*;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;// This class represents the rules of the quiz.public class Rules extends JFrame implements ActionListener {    // Declare the instance variables.    JButton start, back;    String name;    // This constructor initializes the instance variables and creates the GUI.    Rules(String name){        this.name = name;                 // Create a custom JPanel with a gradient background        JPanel contentPanel = new JPanel() {            @Override            protected void paintComponent(Graphics g) {                super.paintComponent(g);                // Define your gradient colors and direction                                 // Using hexadecimal values to define colors        int blueHex = 0x0000FF; // Blue in hexadecimal (R=0, G=0, B=255)        int cyanHex = 0x00FFFF; // Cyan in hexadecimal (R=0, G=255, B=255)        // Creating Color objects using hexadecimal values        Color blueColor = new Color(blueHex);        Color cyanColor = new Color(cyanHex);        // Creating a GradientPaint using Color objects        GradientPaint gradient = new GradientPaint(0, 0, blueColor, getWidth(), getHeight(), cyanColor);       Graphics2D g2d = (Graphics2D) g;                g2d.setPaint(gradient);                g2d.fillRect(0, 0, getWidth(), getHeight());            }        };        contentPanel.setLayout(null);        setContentPane(contentPanel);        // Create a label to display the welcome message.        JLabel heading = new JLabel("Welcome, "+ name +" to QUIZ TEST");        heading.setBounds(90,30,800,50);        heading.setFont( new Font("Viner Hand ITC", Font.BOLD, 50));        heading.setForeground( Color.WHITE);        add(heading);        // Create a label to display the rules of the quiz.        JLabel rules = new JLabel();        rules.setBounds(70,60,700,350);        rules.setFont( new Font("Tahoma", Font.PLAIN, 18));        rules.setForeground( Color.WHITE);        rules.setText(               "<html>"+                 "1. The app will have a total of 10 questions, each with 4 answer choices." + "<br><br>" +                "2. For each correct answer, you will earn 10 points." + "<br><br>" +                "3. For each wromg answer, you will earn 0 points." + "<br><br>" +                "4. You will have 15 seconds to answer each question." + "<br><br>" +                "5. You can retake the quiz as many times as they like." + "<br><br>" +                "6. The app will keep track of the user's scores and progress over time." + "<br><br>" +                "<html>"        );        add(rules);        // Create a button to start the quiz.        start = new JButton("Start");        start.setBounds(550,400,150,40);        start.setBackground( Color.blue);        start.setForeground(Color.WHITE);        start.setFont(new Font("Times New Roman", Font.BOLD,24));        start.addActionListener(this);        add(start);        // Create a button to go back to the login screen.        back = new JButton("Back");        back.setBounds(250,400,150,40);        back.setBackground( Color.blue);        back.setForeground(Color.WHITE);        back.setFocusPainted(false);        back.setFont(new Font("Times New Roman", Font.BOLD,24));        back.addActionListener(this);        add(back);        // Set the window properties.//        setSize(800,650);//        setLocation(350,100);        setSize(1000,500);        setLocation(200,150);        setLayout(null);        setUndecorated(true);        setVisible(true);    }    // This method is called when a button is clicked.    @Override    public void actionPerformed(ActionEvent e) {        if (e.getSource() == start){            setVisible(false);            new Quiz(name);        }else {            setVisible(false);            new Login();        }    }    // This method is the main entry point of the program.    public static void main(String[] args) {        new Rules("User");    }}